import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Navbar, Nav, Card, Button, Form, Row, Col } from 'react-bootstrap'
import axios from 'axios'


class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      "companyList" : ["test1", "test2"],
      "downloadCompany" : ""
    }
  }

  componentDidMount() {
    axios.get('http://192.168.5.179:8000/companies')
    .then(function(res) {
      console.log("1", res)
      this.setState({"companyList": res.data.items})
    })
  }

  handleDownloadCompanyChange = (event) => {

  }

  handleSubmitUpload = (event) => {
    event.preventDefault();
    console.log("2", event)
  }

  handleSubmitDownload = (event) => {
    event.preventDefault();
    console.log("2", event.currentTarget.downloadForm.companySelect)
  }



  render() {
    return (
      <div className="App">
        <Navbar bg="dark" variant="dark">
            <Navbar.Brand>Demo</Navbar.Brand>
        </Navbar>

        <div>
          <br/>
          <br/>
          <Card style={{ width: '75%', margin: 'auto' }}>
            <Card.Header>
              <Card.Title>Upload</Card.Title>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={this.handleSubmitUpload}>
                <Row>
                  <Form.Group as={Col} controlId="downloadForm.companySelect">
                    <Form.Label>Company</Form.Label>
                    <Form.Control as="select">
                    {
                      this.state.companyList.map((item) => <option>{item}</option>)
                    }
                    </Form.Control>
                  </Form.Group>
                  <Form.Group as={Col} controlId="downloadForm.Select">
                    <Form.Label>File</Form.Label>
                    <Form.Control type="file" placeholder="Choose file" />
                  </Form.Group>
                  <Col>
                  <Button variant="primary">Upload</Button>
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>
          <br/>
          <br/>
          <Card style={{ width: '75%', margin: 'auto' }}>
            <Card.Header>
              <Card.Title>Download</Card.Title>
            </Card.Header>
            <Card.Body>
                <Form onSubmit={this.handleSubmitDownload}>
                  <Row>
                    <Form.Group as={Col} controlId="downloadForm.companySelect">
                      <Form.Label>Company</Form.Label>
                      <Form.Control as="select" value={this.state.downloadCompany} onChange={this.handleDownloadCompanyChange}>
                      {
                        this.state.companyList.map((item) => <option>{item}</option>)
                      }
                      </Form.Control>
                    </Form.Group>
                    <Col>
                    <Button variant="primary" type="submit">Download</Button>
                    </Col>
                  </Row>
                </Form>

            </Card.Body>
          </Card>
        </div>
      </div>
    );
  }
}

export default App;
