import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Navbar, Nav, Card, Button, Form, Row, Col } from 'react-bootstrap'
import axios from 'axios'


class App extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      "companyList" : ["test1", "test2"]
    }
  }

  componentDidMount() {
    axios.get('http://192.168.5.179:8000/companies')
    .then(function(res) {
      console.log("1", res)
      this.setState({"companyList": res.data.items})
    })
  }

  render() {
    return (
      <div className="App">
        <Navbar bg="dark" variant="dark">
            <Navbar.Brand>Demo</Navbar.Brand>
        </Navbar>

        <div>
          <br/>
          <br/>
          <Card style={{ width: '75%', margin: 'auto' }}>
            <Card.Body>
              <Card.Title>Upload</Card.Title>
                <Form>
                  <Form.Group controlId="downloadForm.companySelect">
                    <Form.Label>Company</Form.Label>
                    <Form.Control as="select">
                    {
                      this.state.companyList.map((item) => <option>{item}</option>)
                    }
                    </Form.Control>
                  </Form.Group>
                </Form>
              <Button variant="primary">Upload</Button>
            </Card.Body>
          </Card>
          <br/>
          <br/>
          <Card style={{ width: '75%', margin: 'auto' }}>
            <Card.Body>
              <Card.Title>Download</Card.Title>
                <Form>
                  <Row>
                    <Col>
                    <Form.Group as={Col} controlId="downloadForm.companySelect">
                      <Form.Label>Company</Form.Label>
                      <Form.Control as="select">
                      {
                        this.state.companyList.map((item) => <option>{item}</option>)
                      }
                      </Form.Control>
                    </Form.Group>
                    </Col>
                    <Col>
                    <Button variant="primary">Download</Button>
                    </Col>
                  </Row>
                </Form>

            </Card.Body>
          </Card>
        </div>
      </div>
    );
  }
}

export default App;
